package com.github.service;import com.alibaba.fastjson.JSONObject;import com.github.model.Area;import org.apache.commons.io.FileUtils;import org.springframework.core.io.ClassPathResource;import org.springframework.core.io.Resource;import org.springframework.stereotype.Service;import org.springframework.util.Assert;import javax.annotation.PostConstruct;import java.io.File;import java.io.IOException;@Servicepublic class AreaService {		private static JSONObject areaJsonObject = null;		@PostConstruct	public void postConstruct() throws IOException {		Resource resource = new ClassPathResource("area_data.json");		File areaData = resource.getFile();		areaJsonObject = JSONObject.parseObject(FileUtils.readFileToString(areaData, "UTF-8"));		Assert.notNull(areaJsonObject, "area data should be initial.");	}	    public Area get(Integer code){    	        Integer provinceInteger = (code / 10000) * 10000;        Integer cityInteger = (code / 100) * 100;        String province = areaJsonObject.getString(provinceInteger.toString());        String city = areaJsonObject.getString(cityInteger.toString());        String town = areaJsonObject.getString(code.toString());                return new Area(code, province, city, town);    }    public static void main(String[] args) {        new AreaService().get(510108);    }}